mapper.select:
select 

from user T
where T.id = #{id,jdbcType=VARCHAR}

mapper.insert:
insert into user (id, uname, pwd, 
  status, phone, type
  )
values (#{id,jdbcType=VARCHAR}, #{uname,jdbcType=VARCHAR}, #{pwd,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{phone,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}
  )
